import "../resources/sass/main";

(function ($) {
  $(document).ready(function () {
    // code
    $.get("https://jsonplaceholder.typicode.com/users", function (data) {
      const $ul = $(".list-container ul");

      $ul.on("click", "li", function (event) {
        const $this = $(this);
        // console.log("click", event.currentTarget, event.target);
        const $name = $this.find(".name").text();
        const $star = $this.find("[data-star]");
        const $info = $this.find("[data-info]");
        const $moreInfo = $this.find(".more-info");

        const dataSet = Object.keys(event.target.dataset); // array of data attributes from clicked item

        if (dataSet.includes("star")) {
          $this.toggleClass("active");

          if ($star.hasClass("bi-star")) {
            $star.addClass("bi-star-fill");
            $star.removeClass("bi-star");
          } else if ($star.hasClass("bi-star-fill")) {
            $star.removeClass("bi-star-fill");
            $star.addClass("bi-star");
          }
        } else if (dataSet.includes("info")) {
          if ($moreInfo.hasClass("hidden")) {
            $moreInfo.css("height", "auto");
            $moreInfo.removeClass("hidden");
          } else {
            $moreInfo.addClass("hidden");
            // setTimeout(() => {
            $moreInfo.animate({ height: 0 }, 400);
            // }, 500);
          }
        }
      });

      const listItems = data.map(function (li) {
        const { name, phone, email, company, website, address } = li;
        const { street, suite, city, zipcode } = address;
        const { name: companyName, catchPhrase, bs, someKey = null } = company;

        const formatText = (arr, separator = " ") => arr.filter(Boolean).join(separator);

        return `<li>
          <div>
            <div class="name">${name}</div>
            <div class="phone"><span class="category">Phone:</span> ${phone}</div>
            <div class="more-info hidden">
              <div class="email"><span class="category">Email:</span> ${email}</div>
              <div class="website"><span class="category">Website:</span> ${website}</div>
              <div class="company"><span class="category">Company:</span> ${companyName}</div>
              <div class="catchPhrase"><span class="category">Slogan:</span> ${catchPhrase}</div>
              <div class="bs"><span class="category">What We Provide:</span> ${formatText([bs, someKey])}</div>
              <div class="address"><span class="category">Address:</span> ${formatText(
                [street, suite, city, zipcode],
                ", "
              )}</div>
            </div>
          </div>
          <div class="icons">
            <i data-info class="bi bi-info-circle"></i>
            <i data-star class="bi bi-star"></i>
          </div>
          </li>`;
      });

      $ul.html(listItems);
    });
  });
})(jQuery);
