// test

body {
  height: 100%;
  background-color: #f3f3f3;
  font-family: Arial, Helvetica, sans-serif;
}

.list-container {
  padding: 30px;
  display: flex;
  justify-content: center;

  ul {
    list-style: none;
    // flex-basis: 50%;

    li {
      display: flex;
      justify-content: space-between;
      background: #fff;
      padding: 20px;
      border-radius: 4px;
      transition: 0.5s box-shadow;
      margin-bottom: 20px;
      line-height: 1.5;

      .bi-star,
      .bi-info-circle {
        color: rgb(0, 132, 255);
        color: rgba(191, 64, 128, 1);
      }

      .name {
        font-weight: bold;
      }

      .phone {
        font-size: 14px;
      }

      .icons {
        display: flex;
        align-items: flex-start;

        i {
          padding: 5px;
          cursor: pointer;
        }
      }

      .more-info {
        opacity: 1;
        height: 0;
        transform: scaleY(1);
        transition: 0.5s opacity;
        padding-top: 20px;
        line-height: 1.8;
      }

      .category {
        font-weight: bold;
        color: rgba(191, 64, 128, 1);
      }

      .hidden {
        transform: scaleY(0);
        opacity: 0;
        transition: 0.5s opacity;
      }

      &:hover {
        box-shadow: 0px 5px 10px 1px rgba(0, 0, 0, 0.2);
        box-shadow: 0px 5px 10px 1px rgba(191, 64, 128, 0.2);

        transition: 0.5s box-shadow;
      }

      &.active {
        box-shadow: 0 0 0 2px rgb(0, 132, 255);
        box-shadow: 0 0 0 2px rgba(191, 64, 128, 1);

        .bi-star-fill {
          color: rgb(0, 132, 255);
          color: rgba(191, 64, 128, 1);
        }

        &:hover {
          box-shadow: 0 0 0 2px rgb(0, 132, 255), 0px 0px 10px 1px rgba(0, 0, 0, 0.2);
          box-shadow: 0 0 0 2px rgba(191, 64, 128, 1), 0px 0px 10px 1px rgba(0, 0, 0, 0.2);
        }
      }

      div {
      }
    }
  }
}
